import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';

function Bulb() {

    const[bulbOn, setBulbOn] = useState({img1})

    return (
        <div className='main'>
            <button onClick={img2}>Turn On</button>
            <img src={img1} alt='bulb'/>
            <button onClick={img1}>Turn Off</button>
        </div>
    )
}

export default Bulb;
