import React from 'react';

const Practise = (props) => {
    const inputItem = [ ];
    
    const handleClick = (props) => {
      inputItem.push([...inputItem]);

    }


return  <div>
            <input type="text" placeholder="Enter Item..." value = {inputItem} />
            <button onClick={this.state.handleClick}>Add</button>
        </div>

}

export default Practise;
