import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';


function Bulb() {
    const[on, setOn] = useState(false)

    const HandleOn = () =>{
        setOn(on ?  img2 :  img1)
        console.log('on')
    }


    return (
        <div className='main'>
            <button onClick={HandleOn}>Turn On</button>
            <img src={img1} alt='bulb'/>
            <button onClick={HandleOn}>Turn Off</button>
        </div>
    )
}

export default Bulb;

