import React, { useState } from 'react';
import './style.css';
import List from './List';

export default function Input() {
    const[name, setName] = useState('');
    const[item, setItem] = useState([]);

const handleChange = (e) =>{
    setName({
        name:(e.target.value)
    })

}

const handleClick = () => {
    setItem({
            name: "",
            item: [...item, name]
    })
}
    return (
        <div className='main'>
            <h1>To Do App</h1>
            <input type="text" placeholder='Enter your item' className='input' value={name} onChange={handleChange}/>
            <button className='btn' onClick={handleClick}>Add</button>
            <List  item={name}/>
        </div>
    )
}
