import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';

function Bulb() {

    const[bulbOn, setBulbOn] = useState()
    const[bulbOff, setBulbOff] = useState()

    const LightOn = () => {
        setBulbOn({
            bulbOn: {img2}
        })
    }

    return (
        <div className='main'>
            <button onClick={LightOn}>Turn On</button>
            <p>{img1}</p>
            <button onClick={img1}>Turn Off</button>
        </div>
    )
}

export default Bulb;
