import React, { Component } from 'react';




class ToDo extends Component{
    constructor(props){
        super(props);
        this.state={
            inputItem: "",
            item: []
        }
    }
    
    handleInput = (e) => {
      this.setState({inputItem: e.target.value})
    }

    render(){
        return(
            <div className ="App-header">
                <input type="text"  value={this.state.inputItem} onChange={this.handleInput}/>
                <button>Add</button>
            </div>
        )
    }
}
