import React, { Component } from 'react';
import List from '../Person/List';


 
class Todo extends Component{

    constructor(props) {
    super(props);
        this.state = {
          name: " ",
          itemList: [ ] 
        } 
    }
    handleInput = (e) => {
        this.setState({
            name: e.target.value
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({
            name: " ",
            itemList: [...this.state.itemList, this.state.name]
        })
    }


render(){
return ( 
        <div className="App">
            <input type="text" placeholder="Enter username..." value={this.state.name} onChange={this.handleInput} maxLength={3}/>
            <button onClick={this.handleSubmit}>Submit</button>
            <ul>this.itemList.map((add, index) => <li key={this.index}>{this.add}</li></ul>
        </div>
   );
 }
}

export default Todo;
