import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';


function Bulb() {

    const  [bulbOn, setBulbOn]= useState(img1)


    const HandleOn = () =>{
        setBulbOn({img2})
    }




    return (
        <div className='main'>
            <button onClick={HandleOn}>Turn On</button>
            <p>{bulbOn}</p>
            <button>Turn Off</button>
        </div>
    )
}

export default Bulb;

