import React, { Component } from 'react';
import DisplayName from './displayName';

class InputName extends Component{

    constructor(props){
        super(props);
        this.state={
            input: "",
            item: []
        }
    }

    handleChange=(e)=>{
        this.setState({input: e.target.value})
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        this.setState({
            input:"",
            item: [...this.state.item, this.state.input]
        });
    }

    handleDelete = (i) =>{
        let item = [...this.state.item]
        item.splice(i, 1);
        this.setState({
            item
        })
    }
    render(){
        return(
            <div className="main">
                <div>
                    <h1>ToDo List</h1>
                    <input type="text" value={this.state.input} onChange={this.handleChange} placeholder="Enter Username..."/>
                    <button onClick={this.handleSubmit} disabled={!this.state.input}>Add</button>
                </div>
                <div>
                    <DisplayName item={this.state.item} handleDelete={this.handleDelete}/>
                </div>
            </div>
        )
    }
}

export default InputName;
