import React from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';


function Bulb() {
  
   let bulbOn = document.getElementById('bulb')

    const HandleOn = () =>{
        if (bulbOn.src.match("bulbon")) {
            bulbOn.src = "./assets/bulbon.gif";
            } else {
            bulbOn.src = "./assets/bulboff.gif";
           }
    }


    return (
        <div className='main'>
            <button onClick={HandleOn}>Turn On</button>
            <img src={img1} alt='bulb' id='bulb'/>
            <button onClick={HandleOn}>Turn Off</button>
        </div>
    )
}

export default Bulb;

