import React, { Component } from 'react';
import List from '../Person/List';
import './App.css';


 
class Todo extends Component{

    constructor(props) {
    super(props);
        this.state = {
          item: " ",
          itemList: [ ] 
        } 
    }
    handleInput = (e) => {
        this.setState({item: e.target.value})
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({
            item: " ",
            itemList: [...this.state.itemList, this.state.item]
        })
    }


render(){
return ( 
        <div className="App">
            <input type="text" placeholder="Enter Item..." value={this.state.item} onChange={this.handleInput}/>
            <button onClick={() => this.setState({value: 'X'})}>
            {this.props.value}
            </button>
        </div>
   );
 }
}

export default Todo;
