import React, {useState} from 'react';
import './Restaurant.css';
import MenuAPI from './MenuAPI';
import MenuData from './MenuData';

const navList = (MenuAPI.map((currEle) => {
return currEle.category;
}));

console.log(navList)

const Restaurant = () => {
    const [menuData, setMenuList] = useState(MenuAPI)

    const filterItem = (type) => {
        const updatedList = MenuAPI.filter((element) => {
            return element.category === type
        })

        setMenuList(updatedList);
    }

  return (
            <>
                <div>
                    <nav className='navbar'>
                        <button className='navBtn' onClick={() => setMenuList(MenuAPI)}><span>All</span></button>
                        <button className='navBtn' onClick={() => filterItem('breakfast')}><span>Breakfast</span></button>
                        <button className='navBtn' onClick={() => filterItem('lunch')}><span>Lunch</span></button>
                        <button className='navBtn' onClick={() => filterItem('dinner')}><span>Dinner</span></button>
                    </nav>
                </div>
                    <MenuData menuData = {menuData}/>
            </>
  )
};

export default Restaurant;
