import React, { Component } from 'react';
import DisplayName from './displayName';

class UserName extends Component{
constructor(props){
super(props);
this.state={
    input: "",
    item:[]
}
}
handleInput = (e) => {
 this.setState({input: e.target.value});
}

handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
        input: "",
        item:[...this.state.item, this.state.input]
    })
}

handleDelete = (i) => {
 let item=[...this.state.item]
 item.splice(i, 1);
 this.setState({
     item
 })
}



render(){
    return(
        <div className="main">
                <div>
                    <h1>Input Field</h1>
                    <input type="text"placeholder="Enter your name..." value={this.state.input} onChange={this.handleInput}/>
                    <button type="submit" onClick={this.handleSubmit}>Add</button>
                </div>
                <div>
                    <DisplayName item={this.state.item}/>
                </div>
        </div>

    )
}
}


export default UserName;
