import React, { Component } from 'react';
import List from '../Person/List';


 
class Todo extends Component{

    constructor(props) {
    super(props);
        this.state = {
          item: " ",
          itemList: [ ] 
        } 
    }
    handleInput = (e) => {
        this.setState({item: e.target.value})
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({
            item: " ",
            itemList: [...this.state.itemList, this.state.item]
        })
    }


     displayItem = (props) => {
      this.state({ 
            
              display: <ul>{props.itemList.map((add, index)  => <li key={index}>{add}</li>)}</ul>
      } 
      )
 }


render(){
return ( 
        <div className="App">
            <input type="text" placeholder="Enter username..." value={this.state.item} onChange={this.handleInput}/>
            <button onClick={this.handleSubmit}>Add</button>
            <label itemList={this.state.display} />
        </div>
   );
 }
}

export default Todo;
