import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';


function Bulb() {
    const[on, setOn] = useState(img1)

    const HandleOn = () =>{
        setOn(on ?  <img src={img2} alt='bulb'/> :  <img src={img1} alt='bulb'/>)
        console.log('on')
    }


    return (
        <div className='main'>
            <button onClick={HandleOn}>Turn On</button>
            {on}
            <button onClick={HandleOn}>Turn Off</button>
        </div>
    )
}

export default Bulb;

