import React, { Component } from 'react';

 
class Todo extends Component{

    constructor(props) {
    super(props);
        this.state = {
          item: " ",
          itemList: [ ] 
        } 
    }
    onChange = (e) => {
        this.setState({item: e.target.value})
    }
    
    onSubmit = (e) => {
        e.preventDefault();
        this.setState({
            item: " ",
            itemList: [...this.state.itemList, this.state.term]
        })
    }

render(){
return ( 
        <div>
            <input type="text" placeholder="Enter Item..." onChange=""/>
            <button onClick={() => this.setState({value: 'X'})}>
            {this.props.value}
            </button>
        </div>
   );
 }
}

export default Todo;
