import React, {useState} from 'react';
import Cart from './Cart/Cart';

 const MenuData = ({menuData}) => {

  const[cartPage, setcartPage] = useState();

  const handleAddtoCart = () => {
    setcartPage({Cart})
  }

  return (
            <div className="contentWrapper">

                  {
                      menuData.map((currEle) => {
                        const {id, name, image, price} = currEle;
                      
                      return(
                      
                        <div className='background' key={id} >
                          <div className='menuItem'>
                            <h3 className="name">{name}</h3>
                            <img src={image} alt="images" className='image' />
                            <h3 className="price">Price - {price}</h3>
                            <button className='cartBtn' onClick={handleAddtoCart}>Add to cart</button>
                          </div>
                        </div>
                        
                      )
                      }
                      )
                  }
            </div>
  )
}

export default MenuData;

