import React from 'react';

function DisplayItem({itemList, handleDelete}) { // instead of props we can derectly pass value to be refer
    return <ul>
        {itemList.map((add, i) =><li key={i}>{add}<label onClick={() => handleDelete(i)}>&#10060;</label></li> )}
    </ul> 
    // to delete the element pass the function with index so that event will get to know which index value to be deleted.  
}


export default DisplayItem;
