import React from 'react';
import img1 from './assets/bulboff.gif';

function Bulb() {

    return (
        <div className='main'>
            <button onClick={document.getElementById('light').src='./assets/bulbon.gif'}>Turn On</button>
            <img src={img1} alt='bulb' id="light"/>
            <button onClick={document.getElementById('light').src='./assets/bulboff.gif'}>Turn Off</button>
        </div>
    )
}

export default Bulb;
