import React from 'react';

const Practise = (props) => {
  function handleTextChange(e){
    e.preventDefault();
  }
    
    const inputItem = [ ];
    
    function handleClick(e){
     const inputItem = " ";
      inputItem.push({
        label: inputItem});
      e.preventDefault();
      const item = [...inputItem];

      console.log(item);
    }
return ( 
        <div>
            <input type="text" placeholder="Enter Item..." onChange={handleTextChange} value = {inputItem}/>
            <label>{inputItem.label}</label>
            <button onClick={e => handleClick}>Add</button>
        </div>
)
}

export default Practise;
