import React, { Component } from 'react';
import DisplayName from './displayName';

class UserName extends Component{
constructor(props){
super(props);
this.state={
    input: "",
    item:[]
}
}
handleInput(e){
 this.setState({
     input: e.target.value
    });
}



render(){
    return(
        <div className="main">
                <div>
                    <h1>Input Field</h1>
                    <input type="text"placeholder="Enter your name..." onChange={this.handleInput}/>
                    <button type="submit">Add</button>
                </div>
                <div>
                    <DisplayName />
                </div>
        </div>

    )
}
}


export default UserName;
