import React, { Component } from 'react';
import DisplayName from './displayName';

class InputName extends Component{

    constructor(props){
        super(props);
        this.state={
            firstname: "",
            lastname: "",
            item: []
        }
    }

    handleChange=(e)=>{
        this.setState({firstname: e.target.value})
        this.setState({lastname: e.target.value})
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        this.setState({
            firstname:"",
            lastname:"",
            item: [...this.state.item, this.state.firstname, this.state.lastname]
        });
    }

    handleDelete = (i) =>{
        let item = [...this.state.item]
        item.splice(i, 1);
        this.setState({
            item
        })
    }

    handleCondition = () => {
        const validation = /^[a-zA-Z]+$/;
        if((this.state.firstname && this.state.firstname.length<=3) || (this.state.lastname && this.state.lastname.length<=3) || ((this.state.firstname && this.state.lastname) && !validation.test(this.state.input))){
                return"Invalid input"
        }else{
            return""
        }
    }


    render(){
        const {firstname, lastname, item} = this.state
        
        return(

            <div className="main">
                <div>
                    <h1>ToDo List</h1>
                    <input type="text" value={firstname} onChange={this.handleChange} placeholder="Firstname"/>
                    <input type="text" value={lastname} onChange={this.handleChange} placeholder="Lastname"/>
                    <button onClick={this.handleSubmit} disabled={!firstname || !lastname || this.handleCondition()}>Add</button>
                </div>
                    <lable>{this.handleCondition()}</lable>
                <div>
                    <DisplayName item={item} handleDelete={this.handleDelete}/>
                </div>
            </div>
        )
    }
}

export default InputName;
