import React, {Component} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';



export default class 
 extends Component {
     constructor(){
         super();
         this.state={
             bulbOn: img2,
             bulOff: img1
         }
     }
  render() {
    return (
        <div className='main'>
        <button onClick={this.state.bulbOn}>Turn On</button>
        <img src={img1} alt='bulb'/>
        <button onClick={this.state.bulOff}>Turn Off</button>
    </div>
    );
  }
}
