import React, { Component } from 'react';
import './style.css';
import List from './List';

export default class Input extends Component {
    constructor(props){
        super();
        this.state = {
            name: "",
            item: []
        }
    }

 handleChange = (e) =>{
    e.preventDefault();
    this.setState({
        name:(e.target.value)
    })

    console.log("Hi")

}

handleClick = () => {
    this.setState({
            name: "",
            item: [...this.state.item, this.state.name]
    })
}
render(){
    return (
        <div className='main'>
            <h1>To Do App</h1>
                <input type="text" placeholder='Enter your item' className='input' value={this.state.name} onChange={this.handleChange()}/>
                <button className='btn' onClick={this.handleClick()}>Add</button> 
            <List  item={this.state.item}/>
        </div>
    )
}
   
}
