import React from 'react';

function DisplayItem(props) { 
    return <ul>
        {props.itemList.map((add, i) =><li key={i}>{add} <label onClick={() => props.handleDelete(i)}>&#10060;</label></li> )}
    </ul> 
    // to delete the element pass the function with index so that event will get to know which index value to be deleted.  
}


export default DisplayItem;
