import './Cart/Cart.css';
import MenuAPI from './MenuAPI';
import MenuData from './MenuData';

const Navbar = ({filterItem, navItem}) => {

    useEffect(() =>{
        fetch(MenuAPI)
        .then(response => response.json())
        .then(data => setText(data))
    }, [])

    function handleSearch(e){
        setSearchText(e.target.value)
    
    }

    console.log(searchText)

 return (
  <> 
        <nav className='navbar'>
            {navItem.map((currElem) => {
                return(
                    <button 
                        className='navBtn' 
                        onClick={() => filterItem(currElem)}>
                        <span>{currElem}</span>
                    </button>
                    
                )
               })
            }
            <input className="search" placeholder='Search your receipe here...' value={searchText} onChange={handleSearch}/>
            <i className='fas fa-cart-arrow-down cartIcon'></i>
        </nav>
        
        </>
        ) 
    }

export default Navbar;
