import React, { Component } from 'react';

class IoPractice extends Component{
constructor(props){
    super(props);
    this.state={
        username: ''
    }
}

    render(){
        const {username} = this.state
        return(
            <div className ="App-header">
                <p>My first programme</p>
            <div className="inputWrapper">
                <input className="inputBox" type="text" placeholder="Enter Username..." value={username} onChange={this.handleInput}/>
                <button className="btn" onClick={this.handleAdd}  disabled={!this.state.inputItem}>Submit</button>
            </div>
            <div>
                
            </div>
        </div>
        )
    }
}

export default IoPractice;
