import React, {useState} from 'react';
import img1 from './assets/bulboff.gif';
import img2 from './assets/bulbon.gif';

function Bulb() {

    const[bulbOn, setBulbOn] = useState({img2})
    const[bulbOff, setBulbOff] = useState({img1})

    const LightOn = () => {
        setBulbOn({bulbOn})
    }

    const LightOff = () => {
        setBulbOff({bulbOff})
    }

    return (
        <div className='main'>
            <button onClick={LightOn}>Turn On</button>
            <img src={img1} alt='bulb'/>
            <button onClick={LightOff}>Turn Off</button>
        </div>
    )
}

export default Bulb;
